INSERT INTO [dbo].[Locations] ([Id], [LocationName], [LocationDescription], [TypeLocationId], [CityId], [PhotoLocationId]) VALUES (3, N'Королевская галерея Маурицхёйс', N'Государственная коллекция из 841 работы таких деятелей искусства, как Рембрандт, Вермеер и Гольбейн-младший.', NULL, 2, NULL)
INSERT INTO [dbo].[Locations] ([Id], [LocationName], [LocationDescription], [TypeLocationId], [CityId], [PhotoLocationId]) VALUES (4, N'Театр "Опера-комик"', N'«Опера́-Коми́к»(фр. Opéra-Comique; полное название Национальный театр комической оперы, фр. Théâtre national de l’Opéra-Comique) — оперный театр в Париже, основанный в 1715 году и первоначально предназначенный для постановки комических опер.
INSERT INTO [dbo].[Locations] ([Id], [LocationName], [LocationDescription], [TypeLocationId], [CityId], [PhotoLocationId]) VALUES (5, N'Venice Art Biennale', N'Венецианская биеннале — один из самых известных форумов мирового искусства, международная художественная выставка, проводящаяся раз в два года с участием международного жюри.', NULL, NULL, NULL)
INSERT INTO [dbo].[Locations] ([Id], [LocationName], [LocationDescription], [TypeLocationId], [CityId], [PhotoLocationId]) VALUES (6, N'Мариинский театр', N'Государственный академический Мариинский театр (в 1935—1992 годах — Ленинградский ордена Ленина и ордена Октябрьской Революции академический театр оперы и балета имени С. М. Кирова) — театр оперы и балета в Санкт-Петербурге, один из ведущих музыкальных театров России и мира.
