INSERT INTO [dbo].[Locations] ([Id], [LocationName], [LocationDescription], [TypeLocationId], [CityId], [PhotoLocationId]) VALUES (8, N'Национальном Музее Африканской Культуры', N'В Вашингтоне открылся Национальный музей афроамериканской истории и культуры. Архитектурный дизайн музея был разработан Дэвидом Аджайе, автором московской Школы управления "Сколково".', NULL, NULL, NULL)
